mapper.select:

	select * from (
	select /*+INDEX_DESC(vam_author SYS_C007388) */
		rownum as rn, authorid, authorname,nationid, updatedate,regdate
		from vam_author
		where


			authorname like '%'||#{keyword}||'%' and



 	rownum <= #{pageNum}*#{amount}
 	)
 	where rn > (#{pageNum} -1 ) * #{amount}
